import React, { useState } from "react";
import { Card, Button, Row, Col } from "react-bootstrap";
import './Card.css'; // Aseg√∫rate de tener un archivo de CSS

function MovieCard({ movie, onMoreClick }) {
  const [likes, setLikes] = useState(0);
  const [hover, setHover] = useState(true);

  const handleLike = () => setLikes(likes + 1);
  const handleDislike = () => setLikes(likes - 1);

  return (
    <Card
      className="movie-card shadow-lg m-3"
      style={{
        width: "18rem",
        border: hover
          ? `3px solid ${movie.affiliation === "good" ? "blue" : "red"}`
          : "1px solid #ddd",
        transition: "border-color 0.3s, transform 0.3s",
        transform: hover ? "scale(1)" : "scale(1.05)",
      }}
      onMouseEnter={() => setHover(false)}
      onMouseLeave={() => setHover(true)}
    >
      <Card.Img
        variant="top"
        src={hover ? `/images/${movie.poster}` : `/images/${movie.best_character.image}`}
        className="movie-img"
      />
      <Card.Body className="text-center">
        <Card.Title>{hover ? movie.title : movie.best_character.name}</Card.Title>
        <Card.Text className="movie-description">
          {hover ? "Episode: " + movie.episode : movie.best_character.affiliation}
        </Card.Text>
        <Card.Text className="movie-description">
          {hover ? movie.year : movie.best_character.bio}
        </Card.Text>
        <Button variant="primary" onClick={onMoreClick}>
          More...
        </Button>
        <Row className="mt-3">
          <Col>
            <Button variant="success" size="sm" onClick={handleLike}>
              üëç
            </Button>
          </Col>
          <Col>
            <Button variant="danger" size="sm" onClick={handleDislike}>
              üëé
            </Button>
          </Col>
          <Col className="text-muted">{likes}</Col>
        </Row>
      </Card.Body>
    </Card>
  );
}

export default MovieCard;
